declare namespace zetaret.node.api {
	interface StreamMessage {
		type?: string;
		data?: object;
		sid?: string;
	}
	interface CommentStream extends StreamMessage {
		comment?: ChatMessage;
		commentTarget?: ChatMessage;
		commentMid?: string;
	}
	interface IconStream extends StreamMessage {
		icon?: string;
	}
	interface FileStream extends StreamMessage {
		name?: string;
		mime?: string;
		filesize?: number;
	}
	interface ImageStream extends FileStream {
		imageURL?: string;
		imageBase64?: string;
		imageDBKeyChain?: Array<string>;
	}
	interface URLStream extends StreamMessage {
		url?: string;
		urlImage?: ImageStream;
	}
	interface BufferStream extends StreamMessage {
		buffer?: Array<object>;
		bits?: NumBuffer;
		bufferTarget?: StreamMessage;
		bufferSid?: string;
	}
	interface MediaContainer {
		mime: string[];
		supportAudio?: string[];
		supportVideo?: string[];
		metaData?: object;
	}
	interface Codec {
		codecId: string;
		codecName?: string;
		containerSupport?: MediaContainer[];
		sideEffect?: string[];

		encode?: (data: BufferStream) => object[];
		decode?: (data: BufferStream) => object[];
	}
	interface VideoStream extends BufferStream, ImageStream {
		backBuffer?: object[];
		videoResolutionWidth?: number;
		videoResolutionHeight?: number;
		videoResolutionDPI?: number;
		vcodex?: Array<Codec>;
		vdata?: object;
	}
	interface AudioStream extends BufferStream, FileStream {
		audioChannels?: number;
		audioSampleRate?: number;
		audioSampleSize?: number;
		acodex?: Array<Codec>;
		adata?: object;
	}
	interface AVStream extends AudioStream, VideoStream { }
}